#!/usr/bin/env ruby

$neighbours = 5
$number = ""
$start, $end = 0, $neighbours
$biggest_seq = 123
$biggest_pro = 6

($number.size - $neighbours + 1).times do |n|
	y = $number[$start + n, $end]
	p = 1
	y.to_s.split("").each { |i| p *= i.to_i }
	if p > $biggest_pro
		$biggest_pro = p
		$biggest_seq = y
	end
end

$answer = 1
$biggest_seq.split("").collect { |e| e.to_i }.each do |n|
	$answer *= n
end

puts "yer answerrr be: #{$answer}"

#~ yer answerrr be: 99879
